custom-sql.sql:
select eu.*
from esfuser eu , user_ u
where eu.esfUserId = u.userId
and u.firstName like ? and u.lastName like ? and eu.esfUserId in (
select c.esfUserId from esfcard c
where
c.esfCardId in(
select c.esfCardId
from esfentitystate e, esfentitystate e1 where (
e.esfStateId = 1 and e.className = 'it.ethica.esf.model.ESFCard' and c.esfcardId = e.classPK and e.endDate is null
and e1.esfStateId = 1 and e1.className = 'it.ethica.esf.model.ESFUser' and c.esfUserId = e1.classPK and e1.endDate is null
)
)
) and eu.esfUserId not in(
select
ur.esfUserId from esfuseresfuserrole ur where(
ur.esfUserRoleId = ? and ur.esfOrganizationId = ? and endDate is null
)
)

		
		SELECT * FROM esfshooteraffiliationchrono esac
		
		where esac.paymentDate between DATE_FORMAT(?, '%Y-%m-%d') and DATE_FORMAT(SYSDATE(), '%Y-%m-%d')

		and substr(esac.card,1,2) = ?;
